import asyncio
from datetime import datetime

from ..utils import admin_cmd, edit_or_reply, sudo_cmd
from . import CMD_HELP, hmention


@bot.on(admin_cmd(pattern="ping$"))
@bot.on(sudo_cmd(pattern="ping$", allow_sudo=True))
async def _(event):
    if event.fwd_from:
        return
    start = datetime.now()
    event = await edit_or_reply(event, "<b><i>тШЮ Pong!</b></i>", "html")
    end = datetime.now()
    ms = (end - start).microseconds / 1000
    await event.edit(
        f"<b><i>тШЮ Pong</b></i>\nтЮе {ms}\nтЮе <b><i>Bot of {hmention}</b></i>",
        parse_mode="html",
    )


